
public class SwitchCases {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age =25;
		
		switch (age) {
		case 22 :
		    System.out.println("My age is 22");
		    break;
		case 23 :
			System.out.println("My age is 23");
			break;
		case 21:
			System.out.println("My age is 21");
			break;
		case 24:
			System.out.println("My age is 24");
			break;
		default:
			System.out.println("none");
		   
		}
		
		
		
		
		System.out.println("----------------------------");
		
		char grade = 'B';
		
		switch (grade) {
		case 'A' :
			System.out.println("excellent");
			break;
		case 'B' :
			System.out.println("good");
			break;
		case 'C' :
			System.out.println("average");
			break;
		case 'D' :
			System.out.println("need to improment");
			break;
		default:
			System.out.println("fail");
			
			
		}
		

	}

}
